sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Check if the requested_by field has a value
    if (current.requester_name) {
        // Get the sys_id of the referenced user
        var requesterSysId = current.requester_name;

        // Use a GlideRecord query to get the user's name and email from the sys_user table
        var userGR = new GlideRecord('sys_user');
        if (userGR.get(requesterSysId)) {
            var requesterName = userGR.getDisplayValue(); // Get the user's display name
            var requesterEmail = userGR.email; // Get the user's email

            gs.info('Requester Name: ' + requesterName);
            gs.info('Requester Email: ' + requesterEmail);
        } else {
            gs.info('Unable to find the user record for sys_id: ' + requesterSysId);
        }
    } else {
        gs.info('Requester Name or Email is undefined');
    }

    // Check if the short_description field is available before using it
	if ('short_description' in current) {
		gs.info('The Short Description field exists.');
		// Set the Short Description field
		current.short_description = '[Namecard] - ' + (requesterName || 'N/A') + ' + ' + (requesterEmail || 'N/A');

		current.update();
		gs.info('Short Description has been updated to: ' + current.short_description);
	} else {
		gs.info('The Short Description field does not exist.');
	}


})(current, previous);
